import{r as a,o as e,c as r,a as s,F as l,b as d}from"./app.77460253.js";const t={},n=s("h1",{id:"bravely-default-ii"},[s("a",{class:"header-anchor",href:"#bravely-default-ii"},"#"),d(" Bravely Default Ⅱ")],-1),o=s("p",null,[d("TAG: JRPG"),s("br"),d(" 发售日: 2021-02-26"),s("br"),d(" 备注: no data~"),s("br"),d(" 总评人数: 2"),s("br"),d(" 好评人数: 0")],-1),i={id:"db"},c=s("a",{class:"header-anchor",href:"#db"},"#",-1),u=d(" db "),h=s("details",{class:"custom-container details"},[s("summary",null,"小作文警告"),s("p",null,"很标准的一款JRPG，最大的卖点是它的职业系统和坚守/奋勇系统。但由于职业间的不平衡，在最高难度下练好个别职业即可横行无阻，削弱了玩家探索尝试其他职业搭配的兴趣。剧情上很王道，一路少有波澜，直到最后用两个坏结局托出好结局。剧情推进过程中，随着新Boss的出现，之前的Boss都有洗白的迹象。剧情的表现形式如木偶戏一般，演出几乎没有。人物刻画上，所有的反派都很脸谱化，4名主角也是很常见的主角团人物形象，不过稍微立体一点。不同于《八方旅人》中8名主角之间寥寥无几的交互，BD2的4名主角之间的“羁绊对话”贯穿了游戏全程；几乎所有的支线，虽然形式上摆脱不了JRPG支线内容一贯的枯燥乏味，但大多在丰满人物形象。这样下来，即便剧情波澜不惊，玩家也能对主角产生深厚的感情。另外，4名主角在数值上是完全一致且均衡的，也就是4个人没有适合的发展方向，可以任意培养，甚至可以把所有人的所有属性都刷到999。但这就不得不提到游戏相当糟糕的优化，卡顿和较长的加载时间对于有这种练级需求的游戏无疑是折磨。在我看来，BD2是重系统轻剧情的，甚至有剧情为了游戏性服务的感觉。通过剧情我们解锁职业和装备，目的是为了尝试各种职业搭配套路，挑战游戏里最强的精英怪（没错，这游戏的精英怪普遍比Boss强得多）。但如果刷了属性的话，最强的精英怪又能被轻易击倒。总的来说就是各方面都不尽人意吧")],-1),b={id:"sword"},f=s("a",{class:"header-anchor",href:"#sword"},"#",-1),p=d(" sword "),m=s("p",null,"麻瓜剧情，它为什么能这么无聊",-1);t.render=function(d,t){const B=a("Badge");return e(),r(l,null,[n,o,s("h2",i,[c,u,s(B,{type:"danger",text:"-1",vertical:"middle"})]),h,s("h2",b,[f,p,s(B,{type:"danger",text:"-1",vertical:"middle"})]),m],64)};export default t;
