import{r as e,o as a,c as l,a as t,F as r,b as d}from"./app.77460253.js";const p={},h=t("h1",{id:"异度之刃2"},[t("a",{class:"header-anchor",href:"#异度之刃2"},"#"),d(" 异度之刃2")],-1),s=t("p",null,[d("TAG: JRPG"),t("br"),d(" 发售日: 2017-12-01"),t("br"),d(" 备注: no data~"),t("br"),d(" 总评人数: 10"),t("br"),d(" 好评人数: 10")],-1),i={id:"xxd"},n=t("a",{class:"header-anchor",href:"#xxd"},"#",-1),c=d(" xxd "),o=t("p",null,"优缺点都很明显的游戏，给＋1只是因为我永远喜欢吼姆拉/希卡利",-1),u={id:"老王"},b=t("a",{class:"header-anchor",href:"#老王"},"#",-1),x=d(" 老王 "),m=t("p",null,"我买ns真的就只为了xb2",-1),f={id:"swz"},y=t("a",{class:"header-anchor",href:"#swz"},"#",-1),v=d(" swz "),w=t("p",null,"任何一个jrpg玩家都不能不去尝试的游戏。这种地图设计你很难相信真的能出现在游戏中",-1),B={id:"锤"},g=t("a",{class:"header-anchor",href:"#锤"},"#",-1),j=d(" 锤 "),G=t("p",null,"优秀到我可以忽视画质问题和jrpg不讲人话的问题",-1),X={id:"_17"},z=t("a",{class:"header-anchor",href:"#_17"},"#",-1),P=d(" 17 "),R=t("details",{class:"custom-container details"},[t("summary",null,"小作文警告"),t("p",null,"【核爆级剧透预警】"),t("p",null,"实话实说，异度之刃2这个游戏，我在第三章之前根本没明白这架要怎么打，光出场之前我几乎弃坑，第六章去乌龟国之前根本更是没看明白整个剧情是怎么回事。唯一支撑我打下去的是我对制作人的信任。就像有的人永远相信小岛秀夫，而我永远相信高桥哲哉（和本根康之）。"),t("p",null,"而事实上也正是如此，在那段对剧情感到莫名其妙的阶段我非常忐忑，卑微地相信高桥能给我一个好的故事。另一方面，还好本根康之指导的地图设计依旧是顶级的，我不得不承认地图的整体设计比1代更加、更加成功。大幅减少了游玩的不适体验的同时，完全没有丢失1代天马行空的幻想风格。我无法想象一个不受NS垃圾机能限制的XB2世界有多么雄伟壮丽：4K60的古拉大平原、英维迪亚樱花雨、群岛云海天轨——我光是想想就忍不住把任天堂莎了。"),t("p",null,"真正让我对这个游戏改观是在命运的第七章。第六章结尾，吼姆拉被抓走的时候，虽然我真的不懂为什么你们俩个帅逼要抓我lp，但是播片的时候我内心在呐喊：“高桥！我的BGM呢？Counterattack，放啊，为什么不放！？”接着第六章残酷地结束了。第七章开头我基本不记得他们在说什么，小屁孩在失落，我没有，我知道这时候高桥一定会给我充值外挂，所以——少废话，wlp在等我。接下的故事简单而且俗气：灵洞没有lp真的很难打——对不起，尼娅，我喜欢吼姆拉，没有大家——不过是个断崖，看我飞过去。我现在已经不记得看到小绿的时候天是已经黑了还是刚亮，如此沉浸的体验在我所有RPG生涯甚至是游戏生涯中都没有过，高强度XB2的那几天真的是我最近几年最快乐的时候。"),t("p",null,"无关小屁孩的恋爱故事，从第六章开始到结尾，XB2的王道故事真正把少年漫的精髓做了出来。我很难能捡起自己的少年心，能回想起高中那会看路飞指着世界政府的旗帜对乌索普说“狙击王你他妈的把他妈的那个旗子给做了因为罗宾他妈的刚刚说了她想活下去”，能回想起雏田在木叶的空旷废墟里站在佩恩面前守着身后的鸣人，能回想起一护在大诗人给的大块留白中和逐渐消失的露琪亚说再见。此处加一句男人至死是少年真的很土味，可是我真的很感谢monolith，能让我打一管鸡血再续一颗赤子心。真的要比的话XB2不是一个9分游戏，我现在会，以后也会玩到比这个更好的游戏、更好的JRPG，但是这段纯正的热血少年漫体验今后再也不会有了——这是时代的神作，这是我的神作。")],-1),J={id:"sword"},_=t("a",{class:"header-anchor",href:"#sword"},"#",-1),k=d(" sword "),A=t("p",null,"这游戏有缺点吗，我被奈子遮住了眼睛看不见啊",-1),C={id:"db"},F=t("a",{class:"header-anchor",href:"#db"},"#",-1),K=d(" db "),M=t("p",null,"战斗系统复杂，入门难，但可玩性高，非常耐刷。地图设计优秀，但相应地地图指引差到极点。剧情王道但演出好、感染力强，最后一部分内容需要结合前作一起理解",-1),N={id:"bb"},S=t("a",{class:"header-anchor",href:"#bb"},"#",-1),T=d(" bb "),q=t("p",null,"jrpg演出的标杆，相当有趣的战斗和养成系统，音乐和人设无可挑剔",-1),D={id:"bp"},E=t("a",{class:"header-anchor",href:"#bp"},"#",-1),H=d(" bp "),I=t("p",null,"不论旅途中有什么瑕疵，但玩完就只剩十分的感动，无需多言",-1),L={id:"dash"},O=t("a",{class:"header-anchor",href:"#dash"},"#",-1),Q=d(" dash "),U=t("p",null,"美术音乐剧情都太强了，这才是jrpg应该有的样子，限制它的是ns",-1);p.render=function(d,p){const V=e("Badge");return a(),l(r,null,[h,s,t("h2",i,[n,c,t(V,{type:"tip",text:"+1",vertical:"middle"})]),o,t("h2",u,[b,x,t(V,{type:"tip",text:"+1",vertical:"middle"})]),m,t("h2",f,[y,v,t(V,{type:"tip",text:"+1",vertical:"middle"})]),w,t("h2",B,[g,j,t(V,{type:"tip",text:"+1",vertical:"middle"})]),G,t("h2",X,[z,P,t(V,{type:"tip",text:"+1",vertical:"middle"})]),R,t("h2",J,[_,k,t(V,{type:"tip",text:"+1",vertical:"middle"})]),A,t("h2",C,[F,K,t(V,{type:"tip",text:"+1",vertical:"middle"})]),M,t("h2",N,[S,T,t(V,{type:"tip",text:"+1",vertical:"middle"})]),q,t("h2",D,[E,H,t(V,{type:"tip",text:"+1",vertical:"middle"})]),I,t("h2",L,[O,Q,t(V,{type:"tip",text:"+1",vertical:"middle"})]),U],64)};export default p;
