import{r as a,o as r,c as e,a as s,F as t,b as l}from"./app.77460253.js";const d={},n=s("h1",{id:"荒野大镖客-救赎"},[s("a",{class:"header-anchor",href:"#荒野大镖客-救赎"},"#"),l(" 荒野大镖客：救赎")],-1),c=s("p",null,[l("TAG: 开放世界"),s("br"),l(" 发售日: 2010-05-18"),s("br"),l(" 备注: no data~"),s("br"),l(" 总评人数: 1"),s("br"),l(" 好评人数: 1")],-1),o={id:"db"},i=s("a",{class:"header-anchor",href:"#db"},"#",-1),u=l(" db "),b=s("details",{class:"custom-container details"},[s("summary",null,"小作文警告"),s("p",null,"以目前的眼光看画质一般、操作略僵硬，系统相比2代也简化了很多，但主线剧情的悲剧色彩实在令人动容。1912年，牛仔的时代已经过去了，约翰在死里逃生后也和阿比盖尔安顿下来过上了自己的小日子。但自己曾经做的恶并不能一笔勾销，找上门的平克顿让约翰必须直面自己的过去。以约翰的能力，他自然能轻易地摆脱平克顿的纠缠，但为了自己的家人，他只能被平克顿当狗一样使唤，手刃自己曾今的兄弟，对峙自己曾顶礼膜拜的达奇，直到最后被平克顿当作弃子围剿。约翰自己也明白，平克顿不会放过有过去污点的自己，他选择直面自己的死亡来完成救赎，希望于自己的孩子能走上一条不一样的人生道路。但意气用事的杰克也许不能体谅约翰的良苦用心，也许是不能昧着自己的良心，还是走上复仇的老路，又把自己卷入仇恨的循环了。柯南道尔的《血字的研究》也是描述了一个牛仔的复仇故事，结果同样令人感慨。这些复仇故事里我们感受不到快意，只有对陷入不幸的人的悲叹。约翰并不无辜，他8岁成为孤儿，流落到范德林帮被达奇蛊惑作恶多端，但即便洗心革面却依旧不容于社会；罗斯探长表面上代表着政府和资本家倡导的秩序，但他毫不迟疑地用勒索的方式逼迫约翰为他卖命、对底层民众的苦难缺乏了解而自以为是地歧视，轻易打破与约翰的约定，将腐败的公权力丑陋的一面暴露无遗；杰克成长于范德林帮中，小时候就被平克顿威胁过，后来又亲眼目睹了父亲的死亡，母亲也因悲恸早逝，没这样环境下成长起来的他又如何能抑制住自己的仇恨呢？另外，R星擅长的政治讽喻在本作中依旧发挥得很好，社会矛盾、阶级冲突都一展无余")],-1);d.render=function(l,d){const h=a("Badge");return r(),e(t,null,[n,c,s("h2",o,[i,u,s(h,{type:"tip",text:"+1",vertical:"middle"})]),b],64)};export default d;
