import{r as e,o as a,c as l,a as r,F as t,b as d}from"./app.77460253.js";const s={},n=r("h1",{id:"莱莎的炼金工房-常暗女王与秘密藏身处"},[r("a",{class:"header-anchor",href:"#莱莎的炼金工房-常暗女王与秘密藏身处"},"#"),d(" 莱莎的炼金工房 ～常暗女王与秘密藏身处～")],-1),c=r("p",null,[d("TAG: 炼金 / 萌豚"),r("br"),d(" 发售日: 2019-09-26"),r("br"),d(" 备注: no data~"),r("br"),d(" 总评人数: 5"),r("br"),d(" 好评人数: 4")],-1),i={id:"老王"},h=r("a",{class:"header-anchor",href:"#老王"},"#",-1),o=d(" 老王 "),p=r("p",null,"人人都能感受到炼金快乐的一代，推荐萌豚都玩一下，炼金真的很快乐",-1),u={id:"_17"},b=r("a",{class:"header-anchor",href:"#_17"},"#",-1),m=d(" 17 "),f=r("p",null,"萌豚游戏，但是炼金系统挺有意思。到现在为止我都不太能概括这个炼金到底算什么玩法。炼金的玩法结构有点像生存建造：",-1),g=r("blockquote",null,[r("p",null,"采集->建造（炼金）->采集/战斗优化")],-1),v=r("p",null,"作为一个RPG这个循环的天花板基本和通关一致，通关后内容都在配装优化里面，同时通关后炼金系统对装备和战斗的指向性非常明确，这是和一般意义的生存建造相比最核心的区别。炼金调和的设计作为替代建造的系统也有足够的深度可以探索。以最高品质和特性为目标的调和路径是倒推式的，需要从产品的调和树开始，配合现有的原材料分析，最终得到最优的调和路径。以一个周目为时长标准，探索这个优化路径留下了足够的end-game要素",-1),x={id:"blg"},y=r("a",{class:"header-anchor",href:"#blg"},"#",-1),w=d(" blg "),G=r("p",null,"炼金系统很有意思，生态链重金属累积",-1),_={id:"sword"},j=r("a",{class:"header-anchor",href:"#sword"},"#",-1),k=d(" sword "),q=r("p",null,"建议玩菲利斯的炼金工房",-1),A={id:"db"},B=r("a",{class:"header-anchor",href:"#db"},"#",-1),C=d(" db "),D=r("details",{class:"custom-container details"},[r("summary",null,"小作文警告"),r("p",null,"从表面上看，莱莎人设打擦边球，人物塑造刻板，剧情简单，音乐并不令人印象深刻，地图设计一般还到处是空气墙，战斗系统简陋，优化糟糕，但如果深入玩进去的话，就会发现游戏的核心炼金系统很好地把大部分内容联系起来，并赋予了游戏相当高的可玩度。现在回想起来，炼金的游玩体验和宝可梦有异曲同工之妙，但与宝可梦收集、养成、对战的体验不同的是，炼金工房更近似探索-收集-养成-探索的循环：在地图上收集素材——进行调和，获得高品质/特性优异的炼金产品——提升能力/完成剧情目标，解锁新的炼金配方/探索和收集工具/新的地图。由于从消耗品到装备都可以由炼金术合成，也就是说在这个循环中玩家可以自由选取自己喜欢的战斗方式，在一周目的困难难度中也都能游刃有余地应对。另一方面，与宝可梦很多情况下收服只是为了登录图鉴、培育是机械化孵蛋不同，炼金工房里的强力装备需要多种原料合成，中间还有提炼品质和特性的步骤，整个过程中会涉及到非常多种中间体，也就是说大部分的采集素材都会用到。与宝可梦培育6v甚至异色宝可梦然后在对战中施展战术的乐趣相比，炼金工房的调和过程更有思考的乐趣，而之后的战斗就是单方面碾压了。不过当通关解锁传奇难度、开放DLC超难小岛后，玩家则不得不认真地审视整个战斗系统，根据炼金配方规划并做出配套的强力的装备和消耗品，选择合适的人员搭配、职业、站位、道具分配。在这一环节的核心炼金过程中，追求极限炼金产物虽然非常耗时耗力，但不太令人烦躁，因为规划合成路线是非常有意思的一件事，多样的效果和特性让你总有令人瞠目结舌的手段达成目的。如老子莱莎琳·斯托特所说“道炼金术生一，一生二，二生三，三生万物”，只要手里有大锅，一切都能无中生有。当然，也有刷宝石、武器强化-炼成循环、培育噗尼和种地这些需要大量重复操作的内容，在一定程度上也会令人厌烦。总的来说，炼金工房系列是披着美少女皮的有相当深度的硬核游戏，而莱莎这作有因其令人过目不忘的人设和较容易上手的炼金系统，非常值得一玩")],-1);s.render=function(d,s){const F=e("Badge");return a(),l(t,null,[n,c,r("h2",i,[h,o,r(F,{type:"tip",text:"+1",vertical:"middle"})]),p,r("h2",u,[b,m,r(F,{type:"tip",text:"+1",vertical:"middle"})]),f,g,v,r("h2",x,[y,w,r(F,{type:"tip",text:"+1",vertical:"middle"})]),G,r("h2",_,[j,k,r(F,{type:"danger",text:"-1",vertical:"middle"})]),q,r("h2",A,[B,C,r(F,{type:"tip",text:"+1",vertical:"middle"})]),D],64)};export default s;
