import{r as a,o as e,c as s,a as r,F as t,b as d}from"./app.77460253.js";const l={},n=r("h1",{id:"黑暗之魂"},[r("a",{class:"header-anchor",href:"#黑暗之魂"},"#"),d(" 黑暗之魂")],-1),h=r("p",null,[d("TAG: ARPG"),r("br"),d(" 发售日: 2011-09-22"),r("br"),d(" 备注: no data~"),r("br"),d(" 总评人数: 2"),r("br"),d(" 好评人数: 2")],-1),i={id:"ie"},c=r("a",{class:"header-anchor",href:"#ie"},"#",-1),o=d(" ie "),u=r("details",{class:"custom-container details"},[r("summary",null,"小作文警告"),r("p",null,"极其后悔先云的肉鸽之魂，如果可以的话想要清除记忆从零开荒。 难以想象是怎样的天才设计出了这么洗练、一贯而自洽的游戏系统。极高的受伤、死亡惩罚和绿条辅以敌人大量的招式和招式后必定会产生的破绽造就了观察、防御、闪避>>莽的战略思想转变，而这样的战略思想带来的学习和成长（我读懂boss了）的快感又被游戏的困难程度放大成为了无与伦比的成就感。舍弃了传统ACT的连招而用多样的装备和加点来替代，又培养了另一种RPG式成长的爽感。可谓是hard fun的极致。 难以想象是怎样的天才设计出了鬼斧神工，堪称3D的银河恶魔城的地图。银河恶魔城式的弱引导形成了和设计者心照不宣的体验，更可贵的是完全利用了3D的高低立体感。再和篝火机制和不能传送的特点相辅相成，（又一次cue到了hard fun）让人又爱又恨的同时不知不觉间把罗德兰大地逛成了自己的后花园。可谓是有天才能设计出天才的地图，更有天才让所有玩家都能体会到地图的天才之处。 一代更是有着系列最顶尖的史诗感演出。魂让我们看到演出不仅仅是CG，boss的体型，一举一动都能让人代入到一场场大卫vs哥利亚的战斗中（难度和压迫感也起到了这个作用）。 没什么可说的，在RPG和ACT双双显出了一些颓势的当年，魂作为扬弃并融合两者，开创了一类魂ARPG的作品，不得不说有着划时代的意义")],-1),p={id:"dash"},f=r("a",{class:"header-anchor",href:"#dash"},"#",-1),b=d(" dash "),m=r("p",null,"罗德兰就是我家后院",-1);l.render=function(d,l){const G=a("Badge");return e(),s(t,null,[n,h,r("h2",i,[c,o,r(G,{type:"tip",text:"+1",vertical:"middle"})]),u,r("h2",p,[f,b,r(G,{type:"tip",text:"+1",vertical:"middle"})]),m],64)};export default l;
